import React, { useCallback, useState, useEffect } from 'react';

import { GiPerspectiveDiceSixFacesRandom, GiHearts } from 'react-icons/gi';
import { Container, Content, Color, Colors } from './styles';

const Hexcolor: React.FC = () => {
  const [color, setColor] = useState('');
  const [colors, setColors] = useState<String[]>([]);

  const addColor = useCallback(() => {
    setColors([color, ...colors]);
  }, [color, colors]);

  const rand = useCallback((minimal: number, max: number) => {
    return Math.round(Math.random() * (max - minimal)) + minimal;
  }, []);

  const randomIndex = useCallback(
    (codeHex: Array<String>) => {
      return rand(0, codeHex.length - 1);
    },
    [rand],
  );

  const generateColor = useCallback(() => {
    const codeHex = [
      '1',
      '2',
      '3',
      '4',
      '5',
      '6',
      '7',
      '8',
      '9',
      'A',
      'B',
      'C',
      'D',
      'E',
      'F',
    ];

    let hex = '#';
    for (let i = 0; i < 6; i++) {
      const iIndex = randomIndex(codeHex);
      hex += codeHex[iIndex];
    }

    setColor(hex);
  }, [randomIndex]);

  useEffect(() => {
    generateColor();
  }, [generateColor]);

  return (
    <>
      <Container>
        <Content color={color}>
          <Color color={color} />
          <h1>Hexcolor Generator</h1>

          <div>
            <span>{color}</span>
            <GiHearts size={20} onClick={addColor} />
          </div>

          <button type="button" onClick={generateColor}>
            <GiPerspectiveDiceSixFacesRandom size={25} />
            Gerar
          </button>
        </Content>
      </Container>
      <Colors />
    </>
  );
};

export default Hexcolor;
