import React, { useCallback, useState } from 'react';

import { GiPerspectiveDiceSixFacesRandom } from 'react-icons/gi';
import { Container, Content, Color } from './styles';

const Hexcolor: React.FC = () => {
  const [color, setColor] = useState(() => { });

  useCallback(() => {
    function rand(minimal: number, max: number) {
      return Math.round(Math.random() * (max - minimal)) + minimal;
    }

    function randomIndex(codeHex) {
      return rand(0, codeHex.length - 1);
    }

    function getHex() {
      const codeHex = [
        '1',
        '2',
        '3',
        '4',
        '5',
        '6',
        '7',
        '8',
        '9',
        'A',
        'B',
        'C',
        'D',
        'E',
        'F',
      ];

      let hex = '#';
      for (let i = 0; i < 6; i++) {
        const iIndex = randomIndex(codeHex);
        hex += codeHex[iIndex];
      }

      return hex;
    }
  }, []);
  return (
    <Container>
      <Content>
        <Color>
          <span>#495464</span>
        </Color>
        <h1>Hexcolor Generator</h1>

        <button type="button">
          <GiPerspectiveDiceSixFacesRandom size={25} />
          Gerar
        </button>
      </Content>
    </Container>
  );
};

export default Hexcolor;
